mapper.select:
select X.*
	from (
		select ROWNUM as RNUM, A.*
		  from (
			select B_BOARDNO, B_TITLE, B_WRITER, B_REGDATE, B_VIEWCOUNT
			  from TB_BOARD
			 where	1=1

			   and B_TITLE LIKE '%'||#{searchText}||'%'


			   and B_CONTENT LIKE '%'||#{searchText}||'%'


			   and B_WRITER LIKE '%'||#{searchText}||'%'

			 order by B_REGDATE desc
		) A

   	where ROWNUM <= #{end}
) X

where X.RNUM >= #{begin}

select X.*
	from (
		select ROWNUM as RNUM, A.*
		  from (
			select B_BOARDNO, B_TITLE, B_WRITER, B_REGDATE, B_VIEWCOUNT
			  from TB_BOARD
			 where B_WRITER LIKE '%'||#{searchWriter}||'%'
				or B_TITLE LIKE '%'||#{searchTitle}||'%'
				or B_CONTENT LIKE '%'||#{searchContent}||'%'
			 order by B_REGDATE desc
		) A

   	where ROWNUM <= #{end}
) X

where X.RNUM >= #{begin}

mapper.insert:
insert into TB_BOARD(
B_BOARDNO, B_TITLE, B_WRITER, B_CONTENT, U_UNO, B_LANG
) values (
BOARD_SEQ.NEXTVAL, #{title}, #{writer}, #{content}, 2, 'KOR'
)


SELECT MAX(B_BOARDNO) from TB_BOARD

INSERT INTO TB_BOARD_PIC (
	B_SYSNAME, B_ORINAME, B_FILEPATH, B_SIZE, B_BOARDNO
) VALUES (
	#{sysName}, #{oriName}, #{filePath}, #{size}, #{boardNo}
)

mapper.update:
update TB_BOARD
set B_TITLE = #{title},
B_CONTENT = #{content}
where B_BOARDNO = #{boardNo}

mapper.select:
select X.*
from (
select ROWNUM as rnum A.*
from (
select FAQNO, CONTENT, ANSWER
from FAQ
) A

where ROWNUM <= #{end}) X

where X.RNUM >= #{begin}
