mapper.select:
SELECT U_UNO, U_EMAIL, U_PASS, U_NATIONALITY, U_LANGUAGE, U_TOKEN
  FROM TB_USER
 WHERE U_LANGUAGE = #{u_language}
 AND U_EMAIL != #{u_email}

SELECT U_UNO, U_EMAIL, U_PASS, U_NATIONALITY, U_LANGUAGE, U_TOKEN
  FROM TB_USER
 WHERE U_EMAIL = #{u_email}
   AND U_PASS = #{u_pass}

SELECT UP_NO, U_UNO, U_SYSNAME, U_PATH
  FROM TB_USER_PIC
 WHERE U_UNO = #{u_uNo}
